    public function makemoney($cookies,$id,$timestap,$partid =40)
    {
        $curl = curl_init();
        //设置抓取的url
        curl_setopt($curl, CURLOPT_URL, 'http://lc.usth.net.cn/G2S/Learning/StudentLearning.ashx');
        //设置头文件的信息作为数据流输出
        curl_setopt($curl, CURLOPT_HEADER, 1);
        //设置获取的信息以文件流的形式返回，而不是直接输出。
        curl_setopt($curl, CURLOPT_RETURNTRANSFER, 1);
        //发送cookie
        curl_setopt($curl, CURLOPT_COOKIE, $cookies);
        //设置post方式提交
        curl_setopt($curl, CURLOPT_POST, 1);
        //设置post数据
        $post_data =  array(
            "action" => 'GetG2SSetStageLearnPartStudent',
            "fPlanID"=>$partid,
            "fPartID"=>$id,
            "fSecond" => '2700',
            "_"=>'1555376509087'
        );
        curl_setopt($curl, CURLOPT_POSTFIELDS, $post_data);
        //执行命令
        curl_exec($curl);
        $class = '建模';
        if($partid == 40)
            $class = '双创';
        $arr = array('course'=>$class,'id'=>$id);

        $json = json_encode($arr,JSON_UNESCAPED_UNICODE);
       // return json($arr);
        //echo $data;
        //关闭URL请求
        curl_close($curl);
        return $json;
    }
