    /*去除脏数组*/
    function TrimArray($arr){
        if (!is_array($arr)){ return $arr; }
        while (list($key, $value) = each($arr)){
            if (is_array($value)){
                $arr[$key] = TrimArray($value);
            }
            else {
                $arr[$key] = trim($value);
            }
        }
        return $arr;
    }
